function (doc) {
    var fields = doc.getFieldNames().toArray();
    for (var i = 0; i < fields.length; i++) {
        if (fields[i].indexOf('images') > -1) {
            var images = doc.getFieldValues(fields[i]);
            for (var j = 0; j < images.size(); j++) {
                var tmp = 'http://www.amarnatok.com' + images[j];
                images[j] = tmp.replace('jdk.nashorn.internal.runtime.ConsString:', '');
            }
            doc.removeFields(fields[i]);
            doc.addField('images', images);
        }
    }
    return doc;
}
